package sql;

import java.sql.*;

/**
 * @author xasnow
 * @Date 2020/5/12
 */
public class ClobDemo2 {

    public static void main(String[] args) {
        Connection conn = null;
        Statement stmt = null;
        ResultSet rs = null;
        try {
            Class.forName("oracle.jdbc.driver.OracleDriver"); //注册数据库驱动
            try {
                String url = "jdbc:oracle:thin:@localhost:1521:orcl";//orcl要改为数据库名称
                String username = "AIM"; //要改填数据库用户名
                String password = "aim"; //要改填数据库密码
                conn = DriverManager.getConnection(url, username, password); //建立连接
                stmt = conn.createStatement();
                // 执行SQL语句
                rs = getResultSet(stmt, rs);
                conn.close();
                stmt.close();
            } catch (SQLException e) {
                e.printStackTrace();
            }
        } catch (ClassNotFoundException e) {
            e.printStackTrace();
        }
    }

    private static ResultSet getResultSet(Statement stmt, ResultSet rs) throws SQLException {
        for (int i = 0; i < 10; i++) {
            System.out.println(i);
            rs = stmt.executeQuery("declare v_clob clob :='大风起兮云飞扬" + i + "'; begin insert into CLOBTEST values(" + i + ",v_clob); end; ");
        }
        return rs;
    }


}
